mapper.select:
select * from (select a.*,u.username username ,CASE WHEN rr.id>0 THEN 1 ELSE 0 END AS isReadBefore
from wtm_article a LEFT JOIN wtm_official_accounts u ON a.officialAccountId=u.id LEFT JOIN wtm_task_pool pool ON pool.articleId = a.id LEFT JOIN
(select r.id,r.articleId from wtm_article_read_record r WHERE r.memberId=#{memberId}) rr ON rr.articleId=a.id
WHERE pool.isPublishNow=1 AND pool.totalScore>=pool.singleScore AND pool.id>0

  AND a.id=#{articleSearch.articleId}


  AND a.createTime>#{articleSearch.startTime}


  AND a.createTime < #{articleSearch.endTime}


  AND a.officialAccountId=#{articleSearch.officialAccountId}


  AND a.title like concat('%',#{articleSearch.titleKeyWord},'%')

ORDER BY a.isTop DESC,a.createTime DESC) p WHERE p.isReadBefore=0 ORDER BY p.isTop DESC,createTime DESC
