mapper.select:
select m.*,s.memberScore,login.openId,account.* from wtm_member m LEFT JOIN wtm_member_score s ON s.memberId=m.id LEFT JOIN wtm_third_login login ON login.memberId=m.id
LEFT JOIN wtm_official_accounts account ON account.memberId=m.id
WHERE m.telephone=#{telephone}

select m.*,s.memberScore,login.openId,account.* from wtm_member m LEFT JOIN wtm_member_score s ON s.memberId=m.id LEFT JOIN wtm_third_login login ON login.memberId=m.id
LEFT JOIN wtm_official_accounts account ON account.memberId=m.id
 WHERE m.id=#{memberId}
